// Quick lookup by domain
export const DOMAIN_MAP = new Map<string, SupportedSite>(
  SUPPORTED_ECOMMERCE_SITES.map(site => [site.domain, site])
);

/**
 * Check if a domain is in the whitelist
 */
export function isSupportedSite(domain: string): boolean {
  const cleanDomain = domain.toLowerCase().replace(/^www\./, '');
  return DOMAIN_MAP.has(cleanDomain);
}

/**
 * Get site info by domain
 */
export function getSiteInfo(domain: string): SupportedSite | undefined {
  const cleanDomain = domain.toLowerCase().replace(/^www\./, '');
  return DOMAIN_MAP.get(cleanDomain);
}
